            </svg>
        </div>
        
        <div class="controls">
            <button onclick="testFadeScale()">Fade & Scale</button>
            <button onclick="testBounce()">Bounce</button>
            <button onclick="testGlow()">Glow Effect</button>
            <button onclick="testHover()">Hover Effect</button>
            <button onclick="resetLogo()">Reset</button>
        </div>
        
        <div class="status" id="status">
            Ready to test animations! Click any button above.
        </div>
    </div>

    <!-- GSAP CDN -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/gsap/3.12.2/gsap.min.js"></script>
    
    <script>
        // Initialize GSAP
        const logo = document.querySelector('#geez-logo');
        const logoPath = document.querySelector('#logo-path');
        const logoContainer = document.querySelector('#logo-container');
        const status = document.getElementById('status');
        
        // Set initial state
        gsap.set(logoContainer, { scale: 0, opacity: 0 });
        gsap.set(logoPath, { opacity: 0 });
        
        function updateStatus(message) {
            status.textContent = message;
        }
        
        function testFadeScale() {
            updateStatus('Playing Fade & Scale animation...');
            gsap.set(logoContainer, { scale: 0, opacity: 0 });
            gsap.set(logoPath, { opacity: 0 });
            
            gsap.timeline()
                .to(logoContainer, { scale: 1, opacity: 1, duration: 1.5, ease: "back.out(1.7)" })
                .to(logoPath, { opacity: 1, duration: 1, ease: "power2.out" }, "-=1")
                .call(() => updateStatus('Fade & Scale animation completed!'));
        }
        
        function testBounce() {
            updateStatus('Playing Bounce animation...');
            gsap.set(logoContainer, { y: -100, scale: 0.8 });
            gsap.set(logoPath, { opacity: 1 });
            
            gsap.to(logoContainer, { 
                y: 0, 
                scale: 1, 
                duration: 2, 
                ease: "bounce.out",
                onComplete: () => updateStatus('Bounce animation completed!')
            });
        }
        
        function testGlow() {
            updateStatus('Playing Glow animation...');
            gsap.set(logoPath, { opacity: 1 });
            
            gsap.timeline()
                .to(logoPath, { 
                    filter: "drop-shadow(0 0 20px #e3c56b)", 
                    duration: 1, 
                    ease: "power2.inOut" 
                })
                .to(logoPath, { 
                    filter: "drop-shadow(0 0 5px #e3c56b)", 
                    duration: 1, 
                    ease: "power2.inOut",
                    onComplete: () => updateStatus('Glow animation completed!')
                });
        }
        
        function testHover() {
            updateStatus('Hover effect active - move mouse over logo!');
            
            const hoverTl = gsap.timeline({ paused: true });
            hoverTl.to(logoContainer, { scale: 1.1, rotation: 5, duration: 0.3, ease: "power2.out" })
                  .to(logoPath, { fill: "#f4d03f", duration: 0.3, ease: "power2.out" }, 0);
            
            logo.addEventListener('mouseenter', () => hoverTl.play());
            logo.addEventListener('mouseleave', () => hoverTl.reverse());
        }
        
        function resetLogo() {
            updateStatus('Resetting logo...');
            gsap.set(logoContainer, { scale: 1, opacity: 1, rotation: 0, y: 0 });
            gsap.set(logoPath, { opacity: 1, fill: "#e3c56b", filter: "none" });
            updateStatus('Logo reset! Ready for new animations.');
        }
        
        // Auto-play initial animation
        setTimeout(() => {
            testFadeScale();
        }, 500);
    </script>
</body>
</html>
