{{-- 
    Geez Logo SVG Component
    
    This component renders the Geez logo SVG optimized for GSAP animations.
    
    Usage:
    @include('components.geez-logo-svg')
    @include('components.geez-logo-svg', ['size' => 'large'])
    @include('components.geez-logo-svg', ['class' => 'custom-class'])
--}}

@php
    $size = $size ?? 'medium';
    $class = $class ?? '';
    
    $sizeClasses = [
        'small' => 'w-32 h-32',
        'medium' => 'w-64 h-64',
        'large' => 'w-96 h-96',
        'xl' => 'w-[500px] h-[500px]'
    ];
    
    $sizeClass = $sizeClasses[$size] ?? $sizeClasses['medium'];
@endphp

<div class="geez-logo-container {{ $class }}">
    <svg xmlns="http://www.w3.org/2000/svg" 
         xmlns:xlink="http://www.w3.org/1999/xlink" 
         width="500" 
         height="500" 
         viewBox="0 0 375 375" 
         preserveAspectRatio="xMidYMid meet" 
         version="1.0"
         id="geez-logo"
         class="{{ $sizeClass }} max-w-full h-auto"
         role="img"
         aria-label="{{ __('admin.common.geez_logo_alt') }}">
      
      <!-- Define animations and styles -->
      <defs>
        <style>
          .logo-path {
            fill: #e3c56b;
            stroke-dasharray: 0;
            stroke-dashoffset: 0;
          }
          
          .logo-container {
            transform-origin: 50% 50%;
          }
          
          /* Gradient definitions for enhanced effects */
          .logo-gradient {
            fill: url(#logoGradient);
          }
        </style>
        
        <!-- Gradient for enhanced visual effects -->
        <linearGradient id="logoGradient" x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style="stop-color:#f4d03f;stop-opacity:1" />
          <stop offset="50%" style="stop-color:#e3c56b;stop-opacity:1" />
          <stop offset="100%" style="stop-color:#d4af37;stop-opacity:1" />
        </linearGradient>
        
        <!-- Drop shadow filter -->
        <filter id="logoShadow" x="-50%" y="-50%" width="200%" height="200%">
          <feDropShadow dx="0" dy="4" stdDeviation="8" flood-color="rgba(0,0,0,0.1)"/>
        </filter>
        
        <!-- Glow filter -->
        <filter id="logoGlow" x="-50%" y="-50%" width="200%" height="200%">
          <feGaussianBlur stdDeviation="3" result="coloredBlur"/>
          <feMerge> 
            <feMergeNode in="coloredBlur"/>
            <feMergeNode in="SourceGraphic"/>
          </feMerge>
        </filter>
      </defs>
      
